mapper.select:
SELECT 
FROM
oms_order
WHERE
delete_status = 0

  AND order_sn = #{queryParam.orderSn}


  AND `status` = #{queryParam.status}


  AND source_type = #{queryParam.sourceType}


  AND order_type = #{queryParam.orderType}


  AND create_time LIKE concat(#{queryParam.createTime},"%")

order by update_time desc

select 
from oms_order
where order_sn = #{orderSn}

SELECT date_format( payment_time, '%Y-%m-%d' ) AS date, count(*) AS orderCount, sum( pay_amount ) AS orderAmount FROM oms_order WHERE STATUS NOT IN ( 0, 5, 6 ) AND approval_status = 2 AND order_type <> 2
and payment_time >= #{startTime}
and payment_time <= #{endTime}
GROUP BY date_format( payment_time, '%Y-%m-%d' ) ORDER BY payment_time
